		// redraw the main sections anytime the browser resizes
		redraw: function() {

			var ptr = this;
			var h = $(window).height();
			var w = $(window).width();
			 
			// set each section to size of window
			$('section').height(h);
			$('section').width(w);
			
			// // update height of page
			if (this.root.isHome)
				$('#page').height(h);
			else
			 	$('#page').height(h * 9);

			// // // keep content centered vertically
			this.centerContent();
			
			// // // keep background image sized to fit window
			this.resizeBackgrounds();
		},
		
		// vertically center section content
		centerContent: function() {
			var ptr = this;
			
			// home section
			$('#home section .info').each(function() {
				var content = $(this).height();
				var section = $(this).parent().height();
				var nav = 141;
				var gap = (section - (nav + content)) / 2;
				var top = gap;

				// make sure at least minimum distance from top
				var min = 0;
				if (top < min)
					top = min;

				$(this).css('top', top);
			});
			
			// all other sections
			$('article section .content').each(function() {
				var self = this;
				
				// ignore dupes
				if ($(this).hasClass('dupe'))
					return;

				var blockHeight = $(this).siblings('.dupe').find('.block').height();
				var content = $(this).siblings('.dupe').height();
				
				var section = $(this).parent().height();
				var windowH = $(window).height();
				var margin = parseInt($(this).css('margin-left'));
				var nav = 121;
				var gap = (section - (nav + content)) / 2;
				var top = nav + gap;

				// make sure at least minimum distance from top
				var min = nav + (margin / 2);
				var block = $(this).find('.block');

				// difference between min and top
				var diff = min - top;
				if (top < min) {

					// update scrollbar height
					block.css('height', blockHeight - (2 * diff)).jScrollPane({
						clickOnTrack: false,
						hideFocus: true
					});

					top = min;
				} else {
					// restore full block height
					block.css('height', blockHeight);

					// remove scrollbar
					if (block.data('jsp'))
						block.data('jsp').reinitialise();
				}

				$(this).css('top', top);
				
				// check for special layered images and reposition and resize
				$(this).find('.layered').each(function() {
					// original img w and h
					var origw = parseInt($(this).data('width'));
					var origh = parseInt($(this).data('height'));
					
					// new values
					var newh = section - nav - gap;
					var newmargin = gap / -2;

					// if top does not reach min, set height of image to same as left content
					if (diff > 0) {
						newh = $(self).height();
						newmargin = 0;
					}
					
					// limit height to original img height
					if (newh > origh)
						newh = origh;

					// width is always proportional to height
					var neww = newh * origw / origh;
					
					// make it so
					$(this).css('height', newh);
					$(this).css('width', neww);
					$(this).css('margin-top', newmargin);
				});
				
				// for breakthrough only, if height of browser is too short, hide aside text and button
				if ($(this).parent().attr('id') == "breakthrough") {
					if (windowH < 750)
						$(this).find('.layered .inner').hide();
					else
						$(this).find('.layered .inner').show();
				}
			});
		},
		
		resizeBackgrounds: function() {
			var ptr = this;
			
			var w = $(window).width();
			if (w < 1024)
				w = 1024;
			var h = $(window).height();
			
			$('article section .bg img').each(function() {
				var ratio = $(this).attr('width') / $(this).attr('height');

				var imgH = w / ratio;
				
				if (imgH < h) {
					// height-dominant
					$(this).css('height', h);
					var imgW = w * (h / imgH);
					$(this).css('width', imgW);
					
					// if forced left align, then keep left edge at zero
					if ($(this).parent().data('align') == "left") {
						$(this).css('left', 0);
					} else {
						// by default center the bg image horizontally
						$(this).css('left', -.5 * (imgW - w));
					}

				} else {
					// width-dominant
					$(this).css('height', imgH);
					$(this).css('width', w);
					$(this).css('left', 0);
				}
			});
		},

		// go to a specific section
		goto: function(mainId, subId) {
			var ptr = this;
			var w = $(window).width();
			var h = $(window).height();

			// update nav
			this.root.Nav.setActive(mainId, subId);
			
			// recenter content
			this.centerContent();

			// if starting from home, slide up, otherwise just slide to section
			if (this.root.isHome) {
				this.root.isHome = false;

				this.root.isScrolling = true;
				
				// position the selected section to top
				$('#page').addClass('offset' + mainId + '-' + subId);
				
				// slide content up
				$('#content').css('top', h).stop().animate({top: 0}, this.verticalSpeed, this.verticalEasing);

				// slide home up and then hide it
				$('#home').stop().animate({top: -1 * h}, this.verticalSpeed, this.verticalEasing, function() {
					$(this).hide();
				});
				
				// slide nav images down and shrink them
				$('header .main li a img').each(function() {
					var p = .85;
					var w = $(this).width() * p;
					var h = $(this).height() * p;
					$(this).delay(500).animate({
						'margin-top': 91,
						width: w,
						height: h
					}, 500);
				});
				
				// slide header nav up
				$('header').animate({bottom: h - 72}, this.verticalSpeed, this.verticalEasing, function() {
					// swap position from bottom to top
					$('header').css('top', -79);
					
					// mark header as being on top
					$('header').addClass('top');
					
					// update height of page which creates the scrollbar
					$('#page').height(h * 9);
					
					// slide subnav down
					$('header .sub').css('top', 0).show().animate({top: 65}, this.verticalSpeed, this.verticalEasing);

					// jump to specified section
					$('#page').removeClass();
					ptr.contentScroll(mainId, subId, true);

					// init waypoints for each subsection
					ptr.root.Waypoints.init(ptr.root);
					
					// no longer scroll - this needs to be after waypoints have inited
					ptr.root.isScrolling = false;
					
					// show homebutton
					$('#homebutton').addClass('enabled');
					
					// make sure trigger happens
					ptr.root.Triggers.scrollHandler();
				});
			} else {
				// slide to first subsection of selected section
				this.contentScroll(mainId, subId, false);
			}
		},
		
