	/*
	Triggers for animating elements when scrolling
	*/
	Triggers: {
	
		root: null,

		// elements that get animated
		elements: {
			// one time animations
			"trigger": [
				{'selector':'#mission'},
				{'selector':'#breakthrough'},
				{'selector':'#founding'},
				{'selector':'#people'},
				{'selector':'#different'},
				{'selector':'#science'},
				{'selector':'#questions'},
				{'selector':'#workplaces'},
				{'selector':'#society'}
			]
		},
	
		init: function(root) {
			this.root = root;
			var ptr = this;

/*
			// check for css animation support
			this.cssAnimation = Modernizr.cssanimations;
*/
			
/*
			// if ios, turn off css animation for better perfomance
			var iOS = (navigator.userAgent.match(/(iPad|iPhone|iPod)/i) ? true : false);
			if (iOS)
				this.cssAnimation = false;
*/
			// no triggers for ios for better performance
			if (this.root.iOSVersion > 0)
				return;

			// init pre-triggered positions
			var els = this.elements;
			var $el;
			for (var i = 0; i < els.trigger.length; i++) {
				$el = $(els.trigger[i].selector);
				$el.find('.content h1').css('margin-left', -1000);
				$el.find('.content .block').css('margin-left', -1000);
			}
				
			// handle scroll event
			$(window).scroll(function() { ptr.scrollHandler() });
		},
		
		scrollHandler: function() {
			var els = this.elements;
			var elObj;
			
			// check each element to see if it should be triggered
			for (var i = 0; i < els.trigger.length; i++) {
				elObj = els.trigger[i];
				if (this.atTrigger(elObj) == true)
					this.doAnimate(elObj);
			}
		},
		
		// check if an element is at its trigger point for one time animations
		atTrigger: function(elObj) {
			var el = $(elObj.selector);
			
			// if already animated, skip this
			if (el.data('triggered'))
				return false;
	
			// scrolled at or past the element
			var trigger = elObj.ratio == undefined ? .66 : elObj.ratio;
			
			if ($(window).scrollTop() > el.offset().top - ($(window).height() * trigger)) {
				// if element is already off the screen, don't trigger
				var elBottom = el.offset().top + el.height();
				var winTop = $(window).scrollTop();
	
				if (winTop > elBottom)
					return false;
				else
					return true;
			}
			
			return false;
		},
		
		// start one-time animations
		doAnimate: function(elObj) {
			var el = $(elObj.selector);
			
			// mark this as having been triggered
			el.data('triggered', true);
			
			// animate
			el.find('.content h1').delay(250).animate({"margin-left": 0}, 750, 'easeOutExpo');
			el.find('.content .block').delay(500).animate({"margin-left": 0}, 750, 'easeOutExpo');
		},
	
	},
	
	
